Action.ExecScriptExpression:
using System;
using System.Globalization;
using static System.Math;
using static Triggernometry.PluginBridges.BridgeFFXIV;
using static Triggernometry.Interpreter.StaticHelpers;
using Triggernometry;

RealPlugin.plug.RegisterNamedCallback(
    "U7a2_IceSkatingSolver", 
    new Action<object, string>(U7a2_IceSkatingSolver.Callback), 
    null, 
    registrant: "绝伊甸宝宝椅"
);

public static class U7a2_IceSkatingSolver
{
    static int ShivaDir8;
    static double Shivaθ;

    public static void Callback(object _, string command)
    {
        switch (command.ToLower().Trim())
        {
            case "init": Init(); break;
            case "solve": Solve(); break;
            default: throw new ArgumentException("U7a2_IceSkatingSolver: Invalid command. Expecting \"init\" or \"solve\".");
        }
    }

    static void Init()
    {
        ShivaDir8 = (int)ParseDouble(GetScalarVariable(false, "U7a2_滑冰_冰dir8"));
        Shivaθ = (ShivaDir8 - 4) * PI / 4;
    }

    static void Solve()
    {
        var me = GetIdEntity(PlayerHexId);
        var absPos = new Point { X = ParseDouble(me.GetValue("x")), Y = ParseDouble(me.GetValue("y")) };
        var relPos = absPos.AbsToRel();
        if (relPos.Y < -Abs(relPos.X) - 10) // 背面
        {
            RecordState(PositionStateEnum.Back);
            return;
        }
        if (relPos.X * relPos.X + relPos.Y * relPos.Y <= 12 * 12) // 中心
        {
            RecordState(PositionStateEnum.Center);
            return;
        }
        if (!CanSkateToShivaBack(relPos.X, relPos.Y))
        {
            RecordState(PositionStateEnum.Side);
            return;
        }
        var safestRelθ = SolveSafestAngle(relPos.X, relPos.Y);
        var safestAbsθ = safestRelθ + (Shivaθ - PI);
        RecordState(PositionStateEnum.Front, safestAbsθ);
    }

    enum PositionStateEnum { 
        /// <summary> 希瓦身后 </summary>
        Back,
        /// <summary> 希瓦前方，可一次滑冰到背后的区域 </summary>
        Front,
        /// <summary> 场中 12 米内且不在身后，无解</summary>
        Center,
        /// <summary> 希瓦侧面，不可一次滑冰到背后的区域 </summary>
        Side
    }

    static void RecordState(PositionStateEnum state, double θ = 0)
    { 
        SetScalarVariable(false, "U7a2_滑冰_PositionState", state);
        SetScalarVariable(false, "U7a2_滑冰_Safestθ", θ.ToString(CultureInfo.InvariantCulture));
    }

    /// <summary> 
    /// 判断是否可以从指定位置滑向希瓦背后。<br />
    /// 判据为希瓦背后的场地边缘上所有点为圆心、半径 32 的圆周在南侧场地形成的包络线，<br />
    /// 以抛物线近似计算（留有一定容错）。
    /// </summary>
    static bool CanSkateToShivaBack(double relX, double relY) => -relY < 0.03 * relX * relX - 12.1;

    /// <summary> 根据指定位置， </summary>
    static double SolveSafestAngle(double relX, double relY)
    {
        // 希瓦处于相对北 (0, -10)
        // 若当前处于左半场：对称到右半场计算
        if (relX < 0)  
            return -SolveSafestAngle(-relX, relY);
        // 希瓦处于相对北，身后有三条边缘：上方场地圆周、左侧 y = x - 10，右侧 y = -x - 10
        // 若当前处于以身后区域右侧尖端为圆心、32 为半径的圆形区域内：
        //   滑冰角度范围由当前所处位置为圆心、半径 32 的圆周 与 左侧边缘、上方圆周的两个交点确定.
        // 若当前处于以身后区域右侧尖端为圆心、32 为半径的圆形区域外：
        //   滑冰角度范围由当前所处位置为圆心、半径 32 的圆周 与 左右两边缘的交点确定.
        bool isInside = (relX - 8.23) * (relX - 8.23) + (relY + 18.23) * (relY + 18.23) <= 32 * 32;
        Point left = 滑冰圆与左边界交点(relX, relY);
        Point right = isInside ? 滑冰圆与场地交点(relX, relY) : 滑冰圆与右边界交点(relX, relY);
        var leftθ = Atan2(left.X - relX, left.Y - relY);
        var rightθ = Atan2(right.X - relX, right.Y - relY);
        var diff = (rightθ - leftθ + 4 * PI) % (2 * PI);
        if (diff > PI) diff -= 2 * PI;
        return leftθ + diff / 2;
    }

    static Point 滑冰圆与左边界交点(double x0, double y0)
    {
        // (x-x0)^2 + (y-y0)^2 = 32^2 与 y = x - 10 的交点（靠上的）
        var a = 2.0;
        var b = 2 * (10 - x0 - y0);
        var c = y0 * y0 + (x0 - 10) * (x0 - 10) - 1024;
        var y = (-b - Sqrt(b * b - 4 * a * c)) / (2 * a);
        var x = y + 10;
        return new Point { X = x, Y = y };
    }

    static Point 滑冰圆与右边界交点(double x0, double y0)
    {
        // (x-x0)^2 + (y-y0)^2 = 32^2 与 y = x + 10 的交点（靠上的）
        var symPoint = 滑冰圆与左边界交点(-x0, y0);
        return new Point { X = -symPoint.X, Y = symPoint.Y };
    }

    static Point 滑冰圆与场地交点(double x0, double y0)
    {
        // (x-x0)^2 + (y-y0)^2 = 32^2 与 x^2 + y^2 = 20^2 的交点（靠上的）
        var squareSum = x0 * x0 + y0 * y0;
        var a = 4 * squareSum;
        var b = -4 * x0 * (squareSum - 624);
        var c = (squareSum - 624) * (squareSum - 624) - 1600 * y0 * y0;
        var x = (-b + Sqrt(b * b - 4 * a * c)) / (2 * a);
        var y = -Sqrt(400 - x * x);
        return new Point { X = x, Y = y };
    }

    struct Point
    {
        public double X;
        public double Y;

        /// <summary> 以场中为中心、希瓦为相对北，将场地坐标转换为相对坐标。</summary>
        public Point AbsToRel()
        {
            var sin = Sin(Shivaθ);
            var cos = Cos(Shivaθ);
            return new Point { 
                X = -(X - 100) * cos + (Y - 100) * sin,
                Y = -(X - 100) * sin - (Y - 100) * cos,
            };
        }

        /// <summary> 以场中为中心、希瓦为相对北，将相对坐标转换为场地坐标。</summary>
        public Point RelToAbs()
        {
            var sin = Sin(Shivaθ);
            var cos = Cos(Shivaθ);
            return new Point
            {
                X = -X * cos - Y * sin + 100,
                Y =  X * sin - Y * cos + 100,
            };
        }
    }

    static double ParseDouble(object s) => double.Parse(s.ToString(), NumberStyles.Float, CultureInfo.InvariantCulture);
}
